import React, { useState } from "react";
import { ReactComponent as Logo } from "./images/logo-bonus.svg";
import { ReactComponent as Rock } from "./images/icon-rock.svg";
import { ReactComponent as Paper } from "./images/icon-paper.svg";
import { ReactComponent as Scissors } from "./images/icon-scissors.svg";
import { ReactComponent as Lizard } from "./images/icon-lizard.svg";
import { ReactComponent as Spock } from "./images/icon-spock.svg";
import { ReactComponent as Rules } from "./images/image-rules-bonus.svg";
import { ReactComponent as Close } from "./images/icon-close.svg";

const App = () => {
    const [rule, setRule] = useState(false);
    const [played, setPlayed] = useState(0);
    const [bank, setBank] = useState(0);

    let showRules = () => {
        if(rule === true){
            return;
        }else{
            setRule(true);
        }
    }

    let chooseBank = () => {
        setBank(Math.floor(Math.random() * (6 - 1) + 1));
    }

    let setChoice = () => {
        let text = '';
        if(played === 1){
            text = 'scissor'
        }else if(played === 2){
            text = 'paper'
        }else if(played === 3){
            text = 'rock'
        }else if(played === 4){
            text = 'lizard'
        }else if(played === 5){
            text = 'spock'
        }
        return text;
    }

    let calculateWinner = () => {
        let winner = '';
        let p = played;
        let b = bank;
        if(p === 1){
            if(b === 2 || b === 4){
                winner = 'YOU WIN'
            }else if(b === 5 || b === 3){
                winner = 'YOU LOSE'
            }else{
                winner = 'EQUALITY'
            }
        }else if(p === 2){
            if(b === 3 || b === 5){
                winner = 'YOU WIN'
            }else if(b === 4 || b === 1){
                winner = 'YOU LOSE'
            }else{
                winner = 'EQUALITY'
            }
        }else if(p === 3){
            if(b === 4 || b === 1){
                winner = 'YOU WIN'
            }else if(b === 5 || b === 2){
                winner = 'YOU LOSE'
            }else{
                winner = 'EQUALITY'
            }
        }else if(p === 4){
            if(b === 2 || b === 5){
                winner = 'YOU WIN'
            }else if(b === 3 || b === 1){
                winner = 'YOU LOSE'
            }else{
                winner = 'EQUALITY'
            }
        }else{
            if(b === 1 || b === 3){
                winner = 'YOU WIN'
            }else if(b === 2 || b === 4){
                winner = 'YOU LOSE'
            }else{
                winner = 'EQUALITY'
            }
        }
        return winner;
    }

    return(
        <>
            <div className="container">
                <div className="head">
                    <Logo className="logo"/>
                    <div className="score">
                        <p>SCORE</p>
                        <span>12</span>
                    </div>
                </div>
                <div className={played === 0 ? "home" : "home ongame"}>
                    <div 
                        className={played === 0 ? "color scissor" : played === 1 ? "color scissor played" : "none"}
                        onClick={() => {setPlayed(1);
                            chooseBank()}}
                    >
                        <div className="white">
                            <Scissors/>
                        </div>
                    </div>
                    <div 
                        className={played === 0 ? "color paper" :played === 2 ? "color paper played left" : "none"}
                        onClick={() => {
                            setPlayed(2);
                            chooseBank()}}
                    >
                        <div className="white">
                            <Paper/>
                        </div>
                    </div>
                    <div 
                        className={played === 0 ? "color rock" :played === 3 ? "color rock played" : "none"}
                        onClick={() => {setPlayed(3);
                            chooseBank()}}
                    >
                        <div className="white">
                            <Rock/>
                        </div>
                    </div>
                    <div 
                        className={played === 0 ? "color lizard" :played === 4 ? "color lizard played" : "none"}
                        onClick={() => {setPlayed(4);
                            chooseBank()}}
                    >
                        <div className="white">
                            <Lizard/>
                        </div>
                    </div>
                    <div 
                        className={played === 0 ? "color spock" :played === 5 ? "color spock played" : "none"}
                        onClick={() => {setPlayed(5);
                            chooseBank()}}
                    >
                        <div className="white">
                            <Spock/>
                        </div>
                    </div>
                    {
                        played === 0 ?
                        ''
                        : 
                        <div className="bank color bot">
                            <div className="unset white">
                            </div>
                        </div>
                    }
                    {
                    played === 0 ?
                    ''
                    :
                    <div className="result">
                        <span>{calculateWinner()}</span>
                        <div className="play" onClick={() => setPlayed(0)}>
                            <p>PLAY AGAIN</p>
                        </div>
                    </div>
                }   
                </div>
                
                <div className={rule ? "active rules-container" : "rules-container"} onClick={() => showRules()}>
                    <p>RULES</p>
                    {
                        rule ?
                        <>
                            <Rules className="rules"/>
                            <Close 
                                className="close"
                                onClick={() => setRule(false)}
                            />
                        </>
                        :
                        ''
                    }
                </div>
                {
                    rule ?
                    <div className="cache"
                        onClick={() => setRule(false)}    
                    ></div>
                    : 
                    ''
                }
            </div>
        </>
    )
}

export default App;
